import "core-js/modules/es.array.push.js";
import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.filter.js";
import Header from './Header.vue';
export default {
  components: {
    Header
  },
  data() {
    return {
      showCreateDialog: false,
      showEditDialog: false,
      showConfirmDialog: false,
      roles: [{
        label: 'Driver',
        value: 'Driver'
      }, {
        label: 'Barangay Officer',
        value: 'Barangay Officer'
      }, {
        label: 'LGU Officer',
        value: 'LGU Officer'
      }],
      accounts: [{
        name: 'John Doe',
        email: 'john.doe@example.com',
        role: 'Driver'
      }, {
        name: 'Jane Smith',
        email: 'jane.smith@example.com',
        role: 'Barangay Officer'
      }, {
        name: 'Mike Johnson',
        email: 'mike.johnson@example.com',
        role: 'LGU Officer'
      }],
      formData: {
        name: '',
        email: '',
        role: ''
      },
      editData: {},
      selectedAccount: null // Store the account to be deleted
    };
  },
  methods: {
    createAccount() {
      try {
        const newAccount = {
          ...this.formData
        };
        this.accounts.push(newAccount);
        this.resetFormData();
        this.showCreateDialog = false;
        this.showSuccessToast('Account created successfully');
      } catch (error) {
        this.showErrorToast('Failed to create account');
      }
    },
    editAccount(account) {
      this.editData = {
        ...account
      };
      this.showEditDialog = true;
    },
    updateAccount() {
      try {
        const index = this.accounts.findIndex(a => a.email === this.editData.email);
        if (index !== -1) {
          this.accounts.splice(index, 1, {
            ...this.editData
          });
        }
        this.showEditDialog = false;
        this.showSuccessToast('Account updated successfully');
      } catch (error) {
        this.showErrorToast('Failed to update account');
      }
    },
    confirmDeleteAccount(account) {
      this.selectedAccount = account;
      this.showConfirmDialog = true;
    },
    deleteAccount() {
      try {
        this.accounts = this.accounts.filter(a => a.email !== this.selectedAccount.email);
        this.showConfirmDialog = false;
        this.showSuccessToast('Account deleted successfully');
      } catch (error) {
        this.showErrorToast('Failed to delete account');
      }
    },
    resetFormData() {
      this.formData = {
        name: '',
        email: '',
        role: ''
      };
    },
    showSuccessToast(message) {
      this.$refs.toast.add({
        severity: 'success',
        summary: 'Success',
        detail: message,
        life: 3000
      });
    },
    showErrorToast(message) {
      this.$refs.toast.add({
        severity: 'error',
        summary: 'Error',
        detail: message,
        life: 3000
      });
    }
  }
};
